<template>
  <section class="dankal-map-location">
    <img
      :src="icon"
      alt=""
    >
  </section>
</template>

<script>
export default {
  data() {
    return {};
  },

  props: {
    icon: {
      type: String,
      default:
        'data:image/png;base64,',
    },
  },

  computed: {
    map() {
      if (this.$parent.map) {
        return this.$parent.map;
      }

      return null;
    },
  },

  methods: {
    onComponentInitial() {
      const { map } = this;

      if (map) {
        map.on('moveend', this.handlerMapCenter);
        map.on('zoomend', this.handlerMapCenter);
      }
    },

    handlerMapCenter() {
      const center = this.map.getCenter();
      this.$emit('center', center);
    },
  },
};
</script>

<style lang="scss" scope>
.dankal-map-location {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  img {
    width: 0.45rem;
  }
}
</style>
